$(function(){$("#container .leftContent .placeNav li:last-of-type").click(function(){var a=$(this).children("a").children(".down");if(a.hasClass("active")){a.removeClass("active");$(".otherPlaceNav").hide()}else{a.addClass("active");$(".otherPlaceNav").show()}});$(window).scroll(function(){var b=$(window).scrollTop();if(b==0){$("#returnBack").hide()}else{$("#returnBack").show()}});$("#returnBack").click(function(){$(this).goToTop()})});